    @Autowired
    AppKeyService appKeyService;
    @Autowired
    HttpDataService httpDataService;
    @Autowired
    WebSocketDataService webSocketDataService;
    @Autowired
    SYCMConfService sycmConfService;
    @PostMapping("/ezupload")
    public JsonNode ezUpLoad(@Param("appkey") String appkey,@Param("data") String data,@Param("type") String type) throws JsonProcessingException {
        AppKeys key=appKeyService.get(appkey);
        if(key==null){return  new ObjectMapper().readTree("{\"code\":-1,\"message\":\"appKey已过期或不存在\"}");}
        if(type.equals("http")){
            HttpData httpData=new ObjectMapper().readValue(data,HttpData.class);
            if(Pattern.matches("([0-9a-fA-F]{16})+",httpData.response.at("/data").asText())){
                SYCMConf conf=sycmConfService.get();
                SYCMAESDecrypt d=new SYCMAESDecrypt(conf.iv,conf.key);
                httpData.response=new ObjectMapper().readTree(d.AESDecrypt(httpData.response.at("/data").asText()));
            }
            httpData.response= FullDecodeJson.parse(httpData.response);
            httpDataService.insert(httpData);
            return new ObjectMapper().readTree("{\"code\":-1,\"message\":\"success\"}");


        }
        if(type.equals("websocket")){
            WebSocketData webSocketData=new ObjectMapper().readValue(data,WebSocketData.class);
            webSocketData.message=FullDecodeJson.parse(webSocketData.message);
            webSocketDataService.insert(webSocketData);
            return new ObjectMapper().readTree("{\"code\":-1,\"message\":\"success\"}");

        }
        return  new ObjectMapper().readTree("{\"code\":-1,\"message\":\"appKey已过期或不存在\"}");
    }
